<?php 
	/*	
	*	Goodlayers Core Plugin Filter
	*	---------------------------------------------------------------------
	*	This file contains the script to includes necessary function
	* 	for compatibility with goodlayers core plugin
	*	---------------------------------------------------------------------
	*/

	// add body class for page builder
	add_filter('gdlr_core_page_builder_body_class', 'cryptro_page_builder_body_class');
	if( !function_exists('cryptro_page_builder_body_class') ){
		function cryptro_page_builder_body_class( $classes ) {
			return $classes . ' cryptro-body';
		}
	}
	add_filter('gdlr_core_sidebar_class', 'cryptro_gdlr_core_sidebar_class');
	if( !function_exists('cryptro_gdlr_core_sidebar_class') ){
		function cryptro_gdlr_core_sidebar_class( $classes ) {
			return $classes . ' cryptro-sidebar-area';
		}
	}

	// Set the neccessary function to be used in the theme
	add_action('after_setup_theme', 'cryptro_goodlayers_core_setup');
	if( !function_exists( 'cryptro_goodlayers_core_setup' ) ){
		function cryptro_goodlayers_core_setup(){

			// to set the goodlayers core plugin pagination
			add_filter('gdlr_core_pagination_style', 'cryptro_gdlr_core_pagination_style');
			add_filter('gdlr_core_pagination_align', 'cryptro_gdlr_core_pagination_align');

			// set the default social share option
			add_filter('gdlr_core_social_share_list', 'cryptro_gdlr_core_social_share_list');

			// widget title navigation
			add_filter('gdlr_core_widget_title_nav', 'cryptro_gdlr_core_widget_title_nav');

			// single portfolio
			add_filter('gdlr_core_custom_post_slug', 'cryptro_gdlr_core_custom_post_slug', 10, 2);

			add_filter('gdlr_core_portfolio_single_nav', 'cryptro_gdlr_core_portfolio_single_nav');
			add_filter('gdlr_core_portfolio_single_nav_same_term', 'cryptro_gdlr_core_portfolio_single_nav_same_term');
			add_filter('gdlr_core_portfolio_single_related', 'cryptro_gdlr_core_portfolio_single_related');

			// srcset
			add_filter('gdlr_core_enable_srcset', 'cryptro_gdlr_core_enable_srcset');

			// filter for portfolio icon hover
			add_filter('gdlr_core_portfolio_hover_icon_class', 'cryptro_gdlr_core_portfolio_hover_icon_class');

			// youtube cookies
			add_filter('gdlr_core_privacy_settings', 'cryptro_gdlr_core_privacy_settings');
		}
	}

	// youtube no cookies
	if( !function_exists('cryptro_gdlr_core_privacy_settings') ){
		function cryptro_gdlr_core_privacy_settings(){
			if( function_exists('gdlr_core_get_privacy_options') ){
				$privacy_options = gdlr_core_get_privacy_options(1) + gdlr_core_get_privacy_options(2);
				$privacy_settings = array();
				foreach( $privacy_options as $privacy_option_slug => $privacy_option ){
					$privacy_settings[$privacy_option_slug] = cryptro_get_option('plugin', $privacy_option_slug, 
						(empty($privacy_option['default'])? '': $privacy_option['default']));
				}

				return $privacy_settings;
			}
			
			return array();
		}
	}

	// portfolio hover icon class
	if( !function_exists('cryptro_gdlr_core_portfolio_hover_icon_class') ){
		function cryptro_gdlr_core_portfolio_hover_icon_class( $type = '' ){
			if( $type == 'image' ){
			 	return cryptro_get_option('general', 'portfolio-icon-hover-image', '');
			}else if( $type == 'video' ){
				return cryptro_get_option('general', 'portfolio-icon-hover-video', '');
			}else if( $type == 'link' ){
				return cryptro_get_option('general', 'portfolio-icon-hover-link', '');
			}
		}
	}

	// to enable srcset
	if( !function_exists('cryptro_gdlr_core_enable_srcset') ){
		function cryptro_gdlr_core_enable_srcset( $enable ){
			$enable_srcset = cryptro_get_option('plugin', 'enable-srcset', 'disable');
			return ($enable_srcset == 'enable');
		}
	}
			
	// for goodlayers core pagination
	if( !function_exists('cryptro_gdlr_core_pagination_style') ){
		function cryptro_gdlr_core_pagination_style( $style ) {
			return cryptro_get_option('general', 'pagination-style', $style);
		}
	}
	if( !function_exists('cryptro_gdlr_core_pagination_align') ){
		function cryptro_gdlr_core_pagination_align( $align ) {
			return cryptro_get_option('general', 'pagination-align', $align);
		}
	}			

	// custom post type slug
	if( !function_exists('cryptro_gdlr_core_custom_post_slug') ){
		function cryptro_gdlr_core_custom_post_slug( $slug, $type = '' ) {
			if( empty($type) ){ $type = $slug; }

			if( $type == 'portfolio' ){
				$slug = cryptro_get_option('general', 'portfolio-slug', $slug);
			}else if( $type == 'portfolio_category' ){
				$slug = cryptro_get_option('general', 'portfolio-category-slug', $slug);
			}else if( $type == 'portfolio_tag' ){
				$slug = cryptro_get_option('general', 'portfolio-tag-slug', $slug);
			}else if( $type == 'personnel' ){
				$slug = cryptro_get_option('general', 'personnel-slug', $slug);
			}else if( $type == 'personnel_category' ){
				$slug = cryptro_get_option('general', 'personnel-category-slug', $slug);
			}
			
			return $slug;
		}
	}

	// single portfolio
	if( !function_exists('cryptro_gdlr_core_portfolio_single_nav') ){
		function cryptro_gdlr_core_portfolio_single_nav( $check ) {
			return cryptro_get_option('general', 'enable-single-portfolio-navigation', $check);
		}
	}
	if( !function_exists('cryptro_gdlr_core_portfolio_single_nav_same_term') ){
		function cryptro_gdlr_core_portfolio_single_nav_same_term( $check ) {
			return cryptro_get_option('general', 'enable-single-portfolio-navigation-in-same-tag', $check);
		}
	}
	if( !function_exists('cryptro_gdlr_core_portfolio_single_related') ){
		function cryptro_gdlr_core_portfolio_single_related( $args ) {

			$enble_related_portfolio = cryptro_get_option('general', 'enable-related-portfolio', 'enable');
			if( $enble_related_portfolio == 'enable' ){
				$args['portfolio-style'] = cryptro_get_option('general', 'related-portfolio-style', 'grid');
				$args['num-fetch'] = cryptro_get_option('general', 'related-portfolio-num-fetch', 4);
				$args['column-size'] = cryptro_get_option('general', 'related-portfolio-column-size', 15);
				$args['thumbnail-size'] = cryptro_get_option('general', 'related-portfolio-thumbnail-size', 'medium');
				$args['excerpt-number'] = cryptro_get_option('general', 'related-portfolio-num-excerpt', 20);
				$args['column-size'] = cryptro_get_option('general', 'related-portfolio-column-size', 15);

				return $args;
			}else{
				return array();
			}
		}
	}

	// set default social share option
	if( !function_exists('cryptro_gdlr_core_social_share_list') ){
		function cryptro_gdlr_core_social_share_list( $social ) {

			$social['facebook'] = cryptro_get_option('general', 'blog-social-facebook', $social['facebook']);
			$social['linkedin'] = cryptro_get_option('general', 'blog-social-linkedin', $social['linkedin']);
			$social['google-plus'] = cryptro_get_option('general', 'blog-social-google-plus', $social['google-plus']);
			$social['pinterest'] = cryptro_get_option('general', 'blog-social-pinterest', $social['pinterest']);
			$social['stumbleupon'] = cryptro_get_option('general', 'blog-social-stumbleupon', $social['stumbleupon']);
			$social['twitter'] = cryptro_get_option('general', 'blog-social-twitter', $social['twitter']);
			$social['email'] = cryptro_get_option('general', 'blog-social-email', $social['email']);

			return $social;
		}
	}	

	// add filter to register custom fonts for font lists 
	add_filter('gdlr_core_custom_uploaded_font', 'cryptro_register_custom_uploaded_font');
	if( !function_exists('cryptro_register_custom_uploaded_font') ){
		function cryptro_register_custom_uploaded_font( $custom_fonts ){

			$custom_font = cryptro_get_option('typography', 'font-upload');
			if( !empty($custom_font) ){
				foreach($custom_font as $font_option){
					$custom_fonts[$font_option['name']] = array(
						'eot' => $font_option['eot'],
						'ttf' => $font_option['ttf'],
					);
				}
			}		
			
			return $custom_fonts;
			
		} // cryptro_register_custom_uploaded_font
	} // function_exists

	// format widget title
	if( !function_exists('cryptro_gdlr_core_widget_title_nav') ){
		function cryptro_gdlr_core_widget_title_nav( $title_nav ){
			return '</span><span class="gdlr-core-flexslider-nav gdlr-core-plain-style gdlr-core-size-widget">';
		}
	}
	
	// add page builder to post
	add_filter('gdlr_core_page_builder_post_type', 'cryptro_gdlr_core_page_builder_post_type');
	if( !function_exists('cryptro_gdlr_core_page_builder_post_type') ){
		function cryptro_gdlr_core_page_builder_post_type( $post_types ){
			$post_types[] = 'post';
			return $post_types;
		}
	}

	// set lightbox type
	add_filter('gdlr_core_lightbox_type', 'cryptro_gdlr_core_lightbox_type');
	if( !function_exists('cryptro_gdlr_core_lightbox_type') ){
		function cryptro_gdlr_core_lightbox_type(){
			$lightbox_type = cryptro_get_option('plugin', 'lightbox', 'ilightbox');

			if( $lightbox_type == 'ilightbox' ){
				$ilightbox_skin = cryptro_get_option('plugin', 'ilightbox-skin', 'dark');
				return $lightbox_type . '-' . $ilightbox_skin;
			}else{
				return $lightbox_type;
			}
		}
	}
	add_filter('body_class', 'cryptro_lightbox_body_class');
	if( !function_exists('cryptro_lightbox_body_class') ){
		function cryptro_lightbox_body_class($class){
			$link_to_lightbox = cryptro_get_option('plugin', 'link-to-lightbox', 'enable');
			if( $link_to_lightbox == 'enable' ){
				$class[] = 'gdlr-core-link-to-lightbox';
			}
			return $class;
		}
	}

	add_filter('gdlr_core_lightbox_video_autoplay', 'cryptro_lightbox_video_autoplay');
	if( !function_exists('cryptro_lightbox_video_autoplay') ){
		function cryptro_lightbox_video_autoplay($autoplay){
			$video_autoplay = cryptro_get_option('plugin', 'lightbox-video-autoplay', 'enable');
			return ($video_autoplay == 'enable')? true: false;
		}
	}
	
	
